mapper.select:
SELECT consultNo, csDate, csCompanyName, csName, csContact, csEmail, csBdgt, csStatus, csEname FROM RAIN_consult_tbl WHERE 1 = 1
AND ( csCompanyName = '${keyword}' OR csName LIKE CONCAT('%', #{keyword}, '%') OR csEmail = '${keyword}' OR csEname = '${keyword}' )
AND csDate BETWEEN #{firDate} AND #{secDate}

mapper.update:
UPDATE RAIN_consult_tbl SET csStatus = #{csStatus}, csEname = #{csEname}, csResponseDate = #{csResponseDate},
csFailReason = #{csFailReason}, csFailDetailReason = #{csFailDetailReason}
WHERE consultNo = #{consultNo}
